import React from "react";
import { useForm } from "react-hook-form";
import { TextField, Box, Grid, Typography } from "@material-ui/core";

export default function AddArticle() {
    const {register, handleSubmit} = useForm(); 
    const onSubmit = data => console.log(data);
  return (
    <Box px={4} py={2}>
      <form onSubmit={handleSubmit(onSubmit)}>
      <TextField required name="title" label="Title" inputRef={register}  />
      <TextField required name="writer" label="Writer" inputRef={register}  />
      <TextField required multiline rows={3} name="content" label="Content" inputRef={register}  />
      <TextField required multiline rows={10} name="body" label="Article Body" inputRef={register}  />
        <input type="submit" />
      </form>
    </Box>
  );
}
