import React from "react";
import { useForm, Controller } from "react-hook-form";
import {
  TextField,
  Box,
  Grid,
  Button,
  MenuItem,
  Select,
} from "@material-ui/core";
import news from "../TempData/News";
export default function AddArticle() {
  const [category, setCategory] = React.useState("");
  const { register, control, handleSubmit } = useForm();
  const onSubmit = (data) => console.log(data);

  let newsByCategory = news.reduce((r, a) => {
    r[a.category] = [...(r[a.category] || []), a];
    return r;
  }, {});

  return (
    <Box px={15} py={4}>
      <form onSubmit={handleSubmit(onSubmit)}>
        <Grid container spacing={2}>
          <Grid item md={6}>
            <TextField
              fullWidth
              required
              variant="outlined"
              name="title"
              label="Title"
              inputRef={register}
            />
          </Grid>
          <Grid item md={6} />
          <Grid item md={6}>
            <TextField
              fullWidth
              required
              variant="outlined"
              name="writer"
              label="Writer"
              inputRef={register}
            />
          </Grid>
          <Grid item md={6}>
            <Controller
              as={
                <Select>
                  {Object.keys(newsByCategory).map((category) => (
                    <MenuItem value={category}>{category}</MenuItem>
                  ))}
                </Select>
              }
              control={control}
              name="select"
            />
          </Grid>
          <Grid item md={12}>
            <TextField
              fullWidth
              required
              variant="outlined"
              multiline
              rows={3}
              name="content"
              label="Content"
              inputRef={register}
            />
          </Grid>
          <Grid item md={12}>
            <TextField
              fullWidth
              required
              variant="outlined"
              multiline
              rows={10}
              name="body"
              label="Article Body"
              inputRef={register}
            />
          </Grid>
          <Grid item>
            <Button type="submit">add</Button>
          </Grid>
        </Grid>
      </form>
    </Box>
  );
}
