import React from "react";
import { useForm , Controller} from "react-hook-form";
import { TextField, Box, Grid, Button, MenuItem, Select } from "@material-ui/core";
import news from "../TempData/News";
export default function AddArticle() {
  const [category, setCategory] = React.useState("");
  const { register, handleSubmit } = useForm();
  const onSubmit = (data) => console.log(data);

  let newsByCategory = news.reduce((r, a) => {
    r[a.category] = [...(r[a.category] || []), a];
    return r;
  }, {});

  return (
    <Box px={15} py={4}>
      <form onSubmit={handleSubmit(onSubmit)}>
        <Grid container spacing={2}>
          <Grid item md={6}>
            <TextField
              fullWidth
              required
              variant="outlined"
              name="title"
              label="Title"
              inputRef={register}
            />
          </Grid>
          <Grid item md={6} />
          <Grid item md={6}>
            <TextField
              fullWidth
              required
              variant="outlined"
              name="writer"
              label="Writer"
              inputRef={register}
            />
          </Grid>
          <Grid item md={6}>
            <Controller
              name="iceCreamType"
              as={Select}
              options={[
                { value: "chocolate", label: "Chocolate" },
                { value: "strawberry", label: "Strawberry" },
                { value: "vanilla", label: "Vanilla" },
              ]}
              control={register}
              rules={{ required: true }}
            />
            {/* <TextField
              fullWidth
              required
              name="category"
              select
              label="Category"
              value={category}
              onChange={(e) => {
                setCategory(e.target.value);
              }}
              variant="outlined"
              inputRef={register}
            >
              {Object.keys(newsByCategory).map((category) => (
                <MenuItem value={category}>{category}</MenuItem>
              ))}
            </TextField> */}
          </Grid>
          <Grid item md={12}>
            <TextField
              fullWidth
              required
              variant="outlined"
              multiline
              rows={3}
              name="content"
              label="Content"
              inputRef={register}
            />
          </Grid>
          <Grid item md={12}>
            <TextField
              fullWidth
              required
              variant="outlined"
              multiline
              rows={10}
              name="body"
              label="Article Body"
              inputRef={register}
            />
          </Grid>
          <Grid item>
            <Button type="submit">add</Button>
          </Grid>
        </Grid>
      </form>
    </Box>
  );
}
