import React from "react";
import { useForm, Controller } from "react-hook-form";
import {
  TextField,
  Box,
  Grid,
  Fab,
  MenuItem,
  Select,
  FormControl,
  InputLabel,
  Typography
} from "@material-ui/core";

import AddIcon from '@material-ui/icons/Add';
import news from "../TempData/News";
export default function AddArticle() {
  const [category, setCategory] = React.useState("");
  const { register, control, handleSubmit } = useForm();
  const onSubmit = (data) => console.log(data);

  let newsByCategory = news.reduce((r, a) => {
    r[a.category] = [...(r[a.category] || []), a];
    return r;
  }, {});

  return (
    <Box px={15} py={4}>
        <Typography variant="h5">Add an Article</Typography>
      <form onSubmit={handleSubmit(onSubmit)}>
        <Grid container spacing={2}>
          <Grid item md={6}>
            <TextField
              fullWidth
              required
              variant="outlined"
              name="title"
              label="Title"
              inputRef={register}
            />
          </Grid>
          <Grid item md={6} />
          <Grid item md={6}>
            <TextField
              fullWidth
              required
              variant="outlined"
              name="writer"
              label="Writer"
              inputRef={register}
            />
          </Grid>
          <Grid item md={6}>
            <Controller
              as={
                  <FormControl variant="outlined" fullWidth required>
                      <InputLabel id="categoryLabel">Category</InputLabel>
                <Select labelId="categoryLabel" label="Catrgory">
                  {Object.keys(newsByCategory).map((category) => (
                    <MenuItem value={category}>{category}</MenuItem>
                  ))}
                </Select>
                </FormControl>
              }
              control={control}
              name="category"
            />
          </Grid>
          <Grid item md={12}>
            <TextField
              fullWidth
              required
              variant="outlined"
              multiline
              rows={3}
              name="content"
              label="Content"
              inputRef={register}
            />
          </Grid>
          <Grid item md={12}>
            <TextField
              fullWidth
              required
              variant="outlined"
              multiline
              rows={10}
              name="body"
              label="Article Body"
              inputRef={register}
            />
          </Grid>
          <Grid item  style={{float:"right"}}>
          <Fab type="submit" color="primary" aria-label="add">
  <AddIcon />
</Fab>
          </Grid>
        </Grid>
      </form>
    </Box>
  );
}
