import React from "react";
import CssBaseline from "@material-ui/core/CssBaseline";
import Typography from "@material-ui/core/Typography";
import Container from "@material-ui/core/Container";
import Category from "./Category";
import {
  Box,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Grid,
} from "@material-ui/core";
import news from "../TempData/News";
import {
    KeyboardDatePicker,
  } from '@material-ui/pickers';

export default function NewsPreview() {
  let newsByCategory = news.reduce((r, a) => {
    r[a.category] = [...(r[a.category] || []), a];
    return r;
  }, {});

  const [categoryFilter, setCategoryFilter] = React.useState("");
  const [dateFilter, setDateFilter] = React.useState(new Date());
  const [newsDispalyed, setNewsDisplayed] = React.useState(newsByCategory);

  React.useEffect(() => {
    let result = {};
    if (categoryFilter !== "") {
      result[categoryFilter] = newsByCategory[categoryFilter];
    } else {
      result = newsByCategory;
    }
    setNewsDisplayed(result);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [categoryFilter]);

  return (
    <div style={{ backgroundColor: "#efefef" }}>
      <Box px={4} py={2}>
        <Grid container justify="flex-end">
          <Grid item md={2}>
            <FormControl fullWidth>
              <InputLabel id="filterCategory">Category</InputLabel>
              <Select
                fullWidth
                labelId="filterCategory"
                value={categoryFilter}
                onChange={(event) => {
                  setCategoryFilter(event.target.value);
                }}
              >
                <MenuItem value="">
                  <em>All</em>
                </MenuItem>
                {Object.keys(newsByCategory).map((category) => (
                  <MenuItem value={category}>{category}</MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
        <Grid item md={2}>
        <KeyboardDatePicker
          disableToolbar
          variant="inline"
          format="dd/MM/yyyy"
          margin="normal"
          id="date-picker-inline"
          label="Date picker inline"
          value={dateFilter}
          onChange={(date) => {
            setDateFilter(date)}}
          KeyboardButtonProps={{
            'aria-label': 'change date',
          }}
        />
        </Grid>
        </Grid>
        {Object.keys(newsDispalyed).map((category) => (
          <Category name={category} articles={newsDispalyed[category]} />
        ))}
      </Box>
    </div>
  );
}
