import React from "react";
import CssBaseline from "@material-ui/core/CssBaseline";
import Typography from "@material-ui/core/Typography";
import Container from "@material-ui/core/Container";
import Category from "./Category";
import {
  Box,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Grid,
} from "@material-ui/core";
import news from "../TempData/News";

export default function NewsPreview() {
  const [categoryFilter, setCategoryFilter] = React.useState("");
    let newsByCategory = news.reduce((r, a) => {
        r[a.category] = [...(r[a.category] || []), a];
        return r;
      }, {});
    let newDispalyed = newsByCategory;
//   React.useEffect(() => {
//     if(categoryFilter!==""){
//         newDispalyed = {categoryFilter: newsByCategory[categoryFilter]};
//     }
//   },[categoryFilter])



  return (
    <div style={{ backgroundColor: "#efefef" }}>
      <Box px={4} py={2}>
        <Grid container justify="flex-end">
            <Grid item md={2}>
          <FormControl fullWidth>
            <InputLabel id="filterCategory">Category</InputLabel>
            <Select
              fullWidth
              labelId="filterCategory"
              value={categoryFilter}
              onChange={(event) => {
                setCategoryFilter(event.target.value);
              }}
            >
              {Object.keys(newDispalyed).map((category) => (
                <MenuItem value={category}>{category}</MenuItem>
              ))}
            </Select>
          </FormControl>
          </Grid>

        </Grid>
        {Object.keys(newsByCategory).map((category) => (
          <Category name={category} articles={newsByCategory[category]} />
        ))}
      </Box>
    </div>
  );
}
